import { createApi, fetchBaseQuery } from "@reduxjs/toolkit/query/react";
import authApi from "./authApi";

const GRAPH_TYPE = "GRAPH";

const graphApi = createApi({
  reducerPath: "MgGraph",
  baseQuery: fetchBaseQuery({
    baseUrl: "https://graph.microsoft.com/beta/",
    prepareHeaders: (headers, { getState }) => {

    const token = authApi.endpoints.getToken.select()(getState());
    console.log(token);
      //if (token) {
       // headers.set("authorization", `Bearer ${token}`);
     // }

      return headers;
    },
  }),
  tagTypes: [GRAPH_TYPE],
  endpoints: (builder) => ({
    info: builder.query ({
      query: ()=> '/',
      async onQueryStarted(undefined, {dispatch, getState, queryFulfilled}) {
        const result = dispatch(authApi.endpoints.getToken.initiate())
        await queryFulfilled;
        console.log(result);

      }
    }),
    getUsers: builder.query({
      query: () => "users",
    }),
    getUser: builder.query({
      query: (id) =>
        `users/${id}$select=displayName,jobTitle,department,userPrinicpalName`,
      providesTags: (result, err0r, id) => [{ type: GRAPH_TYPE, id }],
    }),
  }),
});

export default graphApi;
export const { useGetUserQuery, useGetUsersQuery, useInfoQuery } = graphApi;
