import { fetchBaseQuery } from "@reduxjs/toolkit/dist/query";
import { getToken } from "./authApi";

console.log(process.env);
const url = `https://${process.env.GRAPH_ENDPOINT}/${process.env.GRAPH_VERSION}`;
const baseQuery = fetchBaseQuery({
  baseUrl: url,
});

const basewWithHeaders = (token) =>
  fetchBaseQuery({
    baseUrl: url,
    prepareHeaders: (headers) => {
      headers.set("authorization", `bearer ${token}`);
      return headers;
    },
  });

const baseQueryWithReauth = async (args, api, extra) => {
  const { error } = await baseQuery(args, api, extra);

  if (error && error.status === 401) {
    const { isSuccess, data } = await api.dispatch(getToken.initiate());

    if (isSuccess) {
      const result = await basewWithHeaders(data)(args, api, extra);
      return result;
    }
  }
};

export default baseQueryWithReauth;
