import { createApi, fetchBaseQuery } from "@reduxjs/toolkit/query/react";
import  query from './fetchWithReauth';

const GRAPH_TYPE = "GRAPH";

const graphApi = createApi({
  reducerPath: "MgGraph",
  baseQuery: fetchBaseQuery({baseUrl: "https://graph.microsoft.com/beta"}),
  tagTypes: [GRAPH_TYPE],
  endpoints: (builder) => ({

    getUsers: builder.query({
      query: () => "users",
    }),
    getUser: builder.query({
      query: (id) =>
        `users/${id}$select=displayName,jobTitle,department,userPrinicpalName`,
      providesTags: (result, error, id) => [{ type: GRAPH_TYPE, id }],
    }),
  }),
});

export default graphApi;
export const { useGetUserQuery, useGetUsersQuery } = graphApi;
