import { fetchBaseQuery } from "@reduxjs/toolkit/dist/query";

import authApi from './authApi'
const url = "https://graph.microsoft.com/v1.0/users"
const baseQuery = fetchBaseQuery({
  baseUrl: url ,
});

const basewWithHeaders = async (token, args, api, extra)=> fetchBaseQuery({
    baseUrl: url,
    prepareHeaders: (headers, getState) => {
        headers.set('authorization', `bearer ${token}`)
        return headers;
    },
    args,
    api,
    extra 
})

const baseQueryWithReauth = async (args, api, extra) => {
  const {error} = await baseQuery(args, api, extra);

  if (error && error.status === 401) {
    //const result = await fetch('http://localhost:5001');
    const {isSuccess, data} = await api.dispatch(authApi.endpoints.getToken.initiate())

    if (isSuccess) {
        const data = await basewWithHeaders(data, args, api, extra)
    }

  }
};

export default baseQueryWithReauth;
